INSERT INTO `command_ref` (`command_ref_id`,`module_id`,`sub_module_id`,`command`,`command_description`,`synopsis`,`requirements`,`ref_url`,`insert_timestamp`,`update_timestamp`) VALUES (1163,7,54,'cyberark_authentication','Module for CyberArk Vault Authentication using PAS Web Services SDK',' Synopsis Authenticates to CyberArk Vault using Privileged Account Security Web Services SDK and creates a session fact that can be used by other modules. It returns an Ansible fact called cyberark session. Every module can use this fact as cyberark session parameter. ',NULL,'https://docs.ansible.com/ansible/latest/modules/cyberark_authentication_module.html#cyberark-authentication-module','2020-04-10 16:59:50','2020-04-10 16:59:51');
