INSERT INTO `command_ref` (`command_ref_id`,`module_id`,`sub_module_id`,`command`,`command_description`,`synopsis`,`requirements`,`ref_url`,`insert_timestamp`,`update_timestamp`) VALUES (1608,12,77,'cnos_template','Manage switch configuration using templates on devices running Lenovo CNOS',' Synopsis This module allows you to work with the running configuration of a switch. It provides a way to execute a set of CNOS commands on a switch by evaluating the current running configuration and executing the commands only if the specific settings have not been already configured. The configuration source can be a set of commands or a template written in the Jinja2 templating language. This module uses SSH to manage network device configuration. The results of the operation will be placed in a directory named results that must be created by the user in their local directory to where the playbook is run. ',NULL,'https://docs.ansible.com/ansible/latest/modules/cnos_template_module.html#cnos-template-module','2020-04-10 17:09:03','2020-04-10 17:09:04');
INSERT INTO `command_ref` (`command_ref_id`,`module_id`,`sub_module_id`,`command`,`command_description`,`synopsis`,`requirements`,`ref_url`,`insert_timestamp`,`update_timestamp`) VALUES (1609,12,77,'cnos_user','Manage the collection of local users on Lenovo CNOS devices',' Synopsis This module provides declarative management of the local usernames configured on Lenovo CNOS devices. It allows playbooks to manage either individual usernames or the collection of usernames in the current running config. It also supports purging usernames from the configuration that are not explicitly defined. ',NULL,'https://docs.ansible.com/ansible/latest/modules/cnos_user_module.html#cnos-user-module','2020-04-10 17:09:04','2020-04-10 17:09:05');
