INSERT INTO `command_ref` (`command_ref_id`,`module_id`,`sub_module_id`,`command`,`command_description`,`synopsis`,`requirements`,`ref_url`,`insert_timestamp`,`update_timestamp`) VALUES (3125,19,158,'debug','Print statements during execution',' Synopsis This module prints statements during execution and can be useful for debugging variables or expressions without necessarily halting the playbook. Useful for debugging together with the when directive. This module is also supported for Windows targets. ',NULL,'https://docs.ansible.com/ansible/latest/modules/debug_module.html#debug-module','2020-04-10 17:42:41','2020-04-10 17:42:44');
