INSERT INTO `command_ref` (`command_ref_id`,`module_id`,`sub_module_id`,`command`,`command_description`,`synopsis`,`requirements`,`ref_url`,`insert_timestamp`,`update_timestamp`) VALUES (3260,21,NULL,'win_shell','Execute shell commands on target hosts',' Synopsis The win shell module takes the command name followed by a list of space delimited arguments. It is similar to the win command module, but runs the command via a shell defaults to PowerShell on the target host. For non Windows targets, use the shell module instead. ',NULL,'https://docs.ansible.com/ansible/latest/modules/win_shell_module.html#win-shell-module','2020-04-10 17:45:39','2020-04-10 17:45:40');
